sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.param = {
		"day" : $sp.getParameter('day'),
		"debug" : $sp.getParameter('debug') == "true"
	};

	data.state = {
		"isLoading" : true
	};
})();
